            <?php
            $comments_hierarchy = [];

            // Group the comments into parent and child
            foreach ($comments as $comment) {
                if ($comment->comment_parent == 0) {
                    // Parent comment
                    $comments_hierarchy[$comment->comment_ID] = [
                        'comment' => $comment,
                        'children' => []
                    ];
                } else {
                    // Child comment, find its parent
                    if (isset($comments_hierarchy[$comment->comment_parent])) {
                        $comments_hierarchy[$comment->comment_parent]['children'][] = $comment;
                    }
                }
            }

            // Loop through the parent comments
            foreach ($comments_hierarchy as $comment_item) {
                $comment = $comment_item['comment'];
                ?>
                <div class="swiper-slide" id="services_comment-<?php echo $comment->comment_ID; ?>">
                    <div class="rounded-[4px] bg-white-100 dark:bg-dark-37 md:px-5 px-2 py-4">
